$(()=>{document.querySelectorAll('a[href="#form"]').forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}),$(".rebate-slider").slick({dots:!1,arrows:!1,infinite:!0,autoplaySpeed:0,speed:3e3,slidesToShow:1,slidesToScroll:1,variableWidth:!0,autoplay:!0,cssEase:"linear"}),$(".brands-slider").slick({dots:!1,arrows:!1,infinite:!0,autoplaySpeed:0,speed:2e3,slidesToShow:1,slidesToScroll:1,variableWidth:!0,autoplay:!0,cssEase:"linear",touchThreshold:5e3,responsive:[{breakpoint:576,settings:{variableWidth:!1,slidesToShow:5}}]}),$(".features-slider").slick({dots:!1,arrows:!0,infinite:!1,autoplaySpeed:5e3,slidesToShow:3,slidesToScroll:1,autoplay:!1,cssEase:"linear",centerMode:!0,centerPadding:"190px",touchThreshold:5e3,initialSlide:1,prevArrow:$(".prev-arrow"),nextArrow:$(".next-arrow"),responsive:[{breakpoint:1200,settings:{slidesToShow:1}},{breakpoint:576,settings:{centerPadding:"10px",slidesToShow:1}}]}),$(".testimonials-slider").slick({dots:!1,arrows:!1,infinite:!1,autoplaySpeed:5e3,slidesToShow:4,slidesToScroll:1,autoplay:!1,centerMode:!0,centerPadding:"200px",touchThreshold:5e3,responsive:[{breakpoint:1200,settings:{slidesToShow:2}},{breakpoint:769,settings:{centerPadding:"100px",slidesToShow:1}},{breakpoint:576,settings:{centerPadding:"10px",slidesToShow:1}}]}),$(".banner-mobile-slider").slick({dots:!0,arrows:!1,infinite:!0,autoplaySpeed:5e3,slidesToShow:1,slidesToScroll:1,variableWidth:!1,autoplay:!0,cssEase:"linear",touchThreshold:5e3}),$(window).on("scroll",()=>{$(this).scrollTop()>=600?$("#back-top").fadeIn(300):$("#back-top").fadeOut(300)}),$("#back-top").on("click",function(){return $("body,html").animate({scrollTop:0},100),!1})});